mapper.select:
select count(*) 
from dwdm.dim_district
where 1=1

	and nation_id = #{nationId}::INT


	and dist_lvl_1 = #{distLvl1}


	and dist_lvl_2 = #{distLvl2}

select * from dwdm.dim_district where 1=1

		and nation_id = #{nationId}::INT


		and dist_lvl_1 = #{distLvl1}


		and dist_lvl_2 = #{distLvl2}


		and continent = #{continentPage}

	order by nation_id, dist_lvl_1, dist_lvl_2, dist_lvl_3 LIMIT #{contentLimit}::INT OFFSET (#{currentPage}::INT-1)*#{contentLimit}::INT
